import React from "react";
import Chart from "react-google-charts";
import { Card, ListGroup } from "react-bootstrap";

const Dashboard = () => {
  return (
    <div className="dash-container">
      <div className="dash-title">
        <h1>Dashboard</h1>
      </div>
      <div className="dash-content">
        <div className="piech">
          <Card style={{ width: "20rem" }}>
            <Card.Header>Email statistics<br/><br/>Last Campaign Performance</Card.Header>
            <ListGroup variant="flush">
              <ListGroup.Item className='chart-padding'>
                <Chart
                  width={"380px"}
                  height={"250px"}
                  chartType="PieChart"
                  loader={<div>Loading Chart</div>}
                  data={[
                    ["Task", "Hours per Day"],
                    ["Open", 3],
                    ["Bounce", 1.5],
                    ["Unsubscribe", 3],
                  ]}
                  options={{
                    allowHtml: 'true',
                    title: "",
                    /*width: "100%",
                    height: "100%",*/
                    padding: "0px",
                    margin: "0px",
                  }}
                  rootProps={{ "data-testid": "1" }}
                />
              </ListGroup.Item>
              <ListGroup.Item>Campaign sent 2 days ago</ListGroup.Item>
            </ListGroup>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
