import React from "react";
import Chart from "react-google-charts";
import { Card, ListGroup } from "react-bootstrap";

const Dashboard = () => {
  return (
    <div className="dash-container">
      <div className="dash-title">
        <h1>dashboard</h1>
      </div>
      <div className="dash-content">
        <Card style={{ width: "35rem" }}>
          <Card.Header>Email statistics</Card.Header>
          <Card.Header>Last Campaign Performance</Card.Header>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <Chart
                width={"500px"}
                height={"200px"}
                chartType="PieChart"
                loader={<div>Loading Chart</div>}
                data={[
                  ["Task", "Hours per Day"],
                  ["Open", 3
                ],
                  ["Bounce", 1.5],
                  ["Unsubscribe", 3],
                ]}
                options={{
                  title: "",
                  width: '300px',
                  height: '180'
                }}
                rootProps={{ "data-testid": "1" }}
              />
            </ListGroup.Item>
            <ListGroup.Item>Campaign sent 2 days ago</ListGroup.Item>
          </ListGroup>
        </Card>
      </div>
    </div>
  );
};

export default Dashboard;
