import React from "react";
import Chart from "react-google-charts";
import { Card, ListGroup } from "react-bootstrap";
import AccessTimeIcon from "@material-ui/icons/AccessTime";
import RestoreIcon from "@material-ui/icons/Restore";

const Dashboard = () => {
  return (
    <div className="dash-container">
      <div className="dash-title">
        <h1>Dashboard</h1>
      </div>
      <div className="dash-content">
        <div className="piech">
          <Card className="dash-card-1">
            <Card.Header>
              <Card.Title>Email statistics</Card.Title>
              <Card.Subtitle className="text-muted">
                Last Campaign Performance
              </Card.Subtitle>
            </Card.Header>
            <ListGroup variant="flush">
              <ListGroup.Item className="chart-padding">
                <Chart
                  width={"500px"}
                  height={"250px"}
                  chartType="PieChart"
                  loader={<div>Loading Chart</div>}
                  data={[
                    ["Task", "Hours per Day"],
                    ["Open", 3],
                    ["Bounce", 1.5],
                    ["Unsubscribe", 3],
                  ]}
                  options={{
                    allowHtml: "true",
                    title: "",
                    /*width: "100%",
                    height: "100%",*/
                    padding: "0px",
                    margin: "0px",
                  }}
                  rootProps={{ "data-testid": "1" }}
                />
              </ListGroup.Item>
              <ListGroup.Item>
                <AccessTimeIcon style={{ marginRight: "10px" }} />
                Campaign sent 2 days ago
              </ListGroup.Item>
            </ListGroup>
          </Card>
        </div>
        <div className="linegph">
          <Card>
            <Card.Header>
              <Card.Title>User Behaviour</Card.Title>
              <Card.Subtitle className="text-muted">
                24 Hours Performance
              </Card.Subtitle>
            </Card.Header>
            <Card.Body>
              <Chart
                width={"600px"}
                height={"400px"}
                chartType="LineChart"
                loader={<div>Loading Chart</div>}
                data={[
                  ["x", "Open", "Click",'Click Second Time'],
                  [0, 0, 0, 0],
                  [1, 10, 5, 13 ],
                  [2, 23, 15, 3],
                  [3, 17, 9, 2 ],
                  [4, 18, 10, 3],
                  [5, 9, 5, 23],
                  [6, 11, 3, 12],
                  [7, 27, 19, 23],
                ]}
                options={{
                  hAxis: {
                    title: "",
                  },
                  vAxis: {
                    title: "",
                  },
                  series: {
                    1: { curveType: "function" },
                    2: { curveType: "function" },
                    3: { curveType: "function" },
                  },
                }}
                rootProps={{ "data-testid": "1" }}
              />
            </Card.Body>
            <Card.Footer className="text-muted">
              <RestoreIcon style={{ marginRight: "10px" }} />
              Updated 3 mins ago
            </Card.Footer>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
